//SkillTreeCalc used to handle all damage bonuses derived from weapon skill tree passives to be applied against combo hits

//Add basic combo hit multiplier
var mult = EvalFormula($#10516612381411113:6064632664408457216$);

//Add basic combo hit multiplier for elemental status effect
var elementalMult = EvalFormula($#10516612381411113:6064632140370085690$);

//Stage multipliers for projectiles, finisher hits, and procs
var projectileMult = 0.0;
var finisherMult = 0.0;
var finisherProcMult = 0.0;
var extFinisherMult = 0.0;
var extFinisherProcMult = 0.0;

//Check for Hit Is Projectile
if(GetRecordTypeId(GetHitSourceRecord(GetHitInProgress())) == GetProjectileTypeId())
{
set mult = mult + EvalFormula($#10516612381411113:6064632704274268161$);
}

//Check for Hit Is Finisher, but not Extended Finisher
if((HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.End") || HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.EndSecondary"))
&&
(!HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.End2") || !HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.End2Secondary")))
{
set mult = mult + EvalFormula($#10516612381411113:6064632664591630340$);

//Check if Finisher Hit is Proc / Deadly
if(HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.ProcHit") || HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.ProcHitSecondary"))
{
set mult = mult + EvalFormula($#10516612381411113:6064632698632863746$);
}

}

//Check for Hit Is Extended Finisher
if(HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.End2") || HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.End2Secondary"))
{
set mult = mult + EvalFormula($#10516612381411113:6064632664867078152$);

//Check for Prism Passive
if(GetEffectCount(GetOwner(),$#30316002:6064631391930811706$) > 0 && GetSkillPurchased(GetInstigator(), GetCurrentWeaponSkilltree(GetInstigator()), $#981011333:6064631439673277683$))
{
set mult = mult + 0.05;
}

//Check for Catalytic Passive
if(GetEffectCount(GetOwner(),$#30316002:6064631391930811706$) > 0 && GetSkillPurchased(GetInstigator(), GetCurrentWeaponSkilltree(GetInstigator()), $#981011333:6064631439962669838$))
{
set mult = mult + EvalFormula($#10516612381411113:6064632140561712963$);
}

//Check if Extended Finisher Hit is Proc / Deadly
if(HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.ProcHit") || HitHasTag(GetHitRecord(GetHitInProgress()), "ComboStep.ProcHitSecondary"))
{
set mult = mult + EvalFormula($#10516612381411113:6064632698634043395$);

//Check if previous hit was also Deadly and attacker has Arcane Amplifier skill
if(GetEffectCount(GetOwner(),$#30316002:6064631391930811706$) > 0 && GetSkillPurchased(GetInstigator(), GetCurrentWeaponSkilltree(GetInstigator()), $#981011333:6064631445112567295$))
{
set mult = mult + 0.25;
}

}

}
//Add all multipliers together for final output
1 + mult + elementalMult
