const Perception = GetStealthPerceptionState(GetTarget(), GetSource());
var Opacity = 1.0;
if(Perception == EStealthPerceptionState::Hidden ||
  	Perception == EStealthPerceptionState::Detected){
  	set Opacity = 0.0;
}else if(Perception == EStealthPerceptionState::Revealed){
  	set Opacity = 0.5;
}
