const woundedThreshold = GetStat(GetInteractionTarget(), $#10516612381411111:6064633339986053691$);
const injuredThreshold = GetStat(GetInteractionTarget(), $#10516612381411111:6064633339983432250$);

const isInjured = hitAmount < woundedThreshold && hitAmount >= injuredThreshold;

