// Amount of times the creature has been hit
const hitAmount = GetStat(GetInteractionTarget(), $#10516612381411111:6064633304797806592$);

// The thresholds for Wounded and Mangled states of the hunting creature. Set via stat initializer on the NPC
const woundedThreshold = GetStat(GetInteractionTarget(), $#10516612381411111:6064633339986053691$);
const mangledThreshold = GetStat(GetInteractionTarget(), $#10516612381411111:6064633339987167804$);

const isWounded = hitAmount < mangledThreshold && hitAmount >= woundedThreshold;

isWounded
